// Helper function to get channels grouped by country
export const getChannelsByCountry = () => {
  const channelsByCountry: Record<string, Channel[]> = {};
  
  tvChannels.forEach(channel => {
    if (!channelsByCountry[channel.country]) {
      channelsByCountry[channel.country] = [];
    }
    channelsByCountry[channel.country].push(channel);
  });
  
  // Sort countries alphabetically and channels within each country
  const sortedChannels: Record<string, Channel[]> = {};
  Object.keys(channelsByCountry)
    .sort()
    .forEach(country => {
      sortedChannels[country] = channelsByCountry[country].sort((a, b) => 
        a.title.localeCompare(b.title)
      );
    });
  
  return sortedChannels;
};

